import React, { useState } from 'react';
import {
  Box,
  Card,
  CardContent,
  TextField,
  Button,
  Typography,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Slider,
  Switch,
  FormControlLabel,
  Alert,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Chip,
  IconButton,
  AppBar,
  Toolbar,
  Divider,
} from '@mui/material';
import {
  ArrowBack,
  QrCode,
  ContentCopy,
  LocationOn,
  Schedule,
  People,
  Settings,
} from '@mui/icons-material';
import { useNavigate } from 'react-router-dom';

interface RoomSettings {
  title: string;
  description: string;
  duration: number; // ÊôÇÈñìÔºàÂàÜÔºâ
  maxParticipants: number;
  isPrivate: boolean;
  allowImages: boolean;
  allowStamps: boolean;
  locationRadius: number; // km
}

const CreateRoom: React.FC = () => {
  const navigate = useNavigate();
  
  const [roomSettings, setRoomSettings] = useState<RoomSettings>({
    title: '',
    description: '',
    duration: 60,
    maxParticipants: 50,
    isPrivate: false,
    allowImages: true,
    allowStamps: true,
    locationRadius: 1,
  });
  
  const [isCreating, setIsCreating] = useState(false);
  const [createdRoom, setCreatedRoom] = useState<{
    id: string;
    inviteCode: string;
    qrCodeUrl: string;
  } | null>(null);
  const [showQRDialog, setShowQRDialog] = useState(false);
  const [error, setError] = useState<string | null>(null);

  const durationOptions = [
    { value: 30, label: '30ÂàÜ' },
    { value: 60, label: '1ÊôÇÈñì' },
    { value: 120, label: '2ÊôÇÈñì' },
    { value: 180, label: '3ÊôÇÈñì' },
    { value: 360, label: '6ÊôÇÈñì' },
    { value: 720, label: '12ÊôÇÈñì' },
    { value: 1440, label: '24ÊôÇÈñì' },
  ];

  const participantOptions = [10, 20, 30, 50, 100];

  const handleInputChange = (field: keyof RoomSettings, value: any) => {
    setRoomSettings(prev => ({
      ...prev,
      [field]: value,
    }));
  };

  const handleCreateRoom = async () => {
    // „Éê„É™„Éá„Éº„Ç∑„Éß„É≥
    if (!roomSettings.title.trim()) {
      setError('„É´„Éº„É†„Çø„Ç§„Éà„É´„ÇíÂÖ•Âäõ„Åó„Å¶„Åè„Å†„Åï„ÅÑ');
      return;
    }

    setIsCreating(true);
    setError(null);

    try {
      // API„Åß„É´„Éº„É†‰ΩúÊàêÔºàÂÆüÈöõ„ÅÆÂÆüË£Ö„Åß„ÅØÈÅ©Âàá„Å™API„Ç®„É≥„Éâ„Éù„Ç§„É≥„Éà„Çí‰ΩøÁî®Ôºâ
      const response = await fetch('/api/rooms', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          ...roomSettings,
          expiresAt: new Date(Date.now() + roomSettings.duration * 60 * 1000).toISOString(),
        }),
      });

      if (!response.ok) {
        throw new Error('„É´„Éº„É†‰ΩúÊàê„Å´Â§±Êïó„Åó„Åæ„Åó„Åü');
      }

      // „Çµ„É≥„Éó„É´„É¨„Çπ„Éù„É≥„ÇπÔºàÂÆüÈöõ„ÅÆAPI„É¨„Çπ„Éù„É≥„Çπ„Å´Âêà„Çè„Åõ„Å¶Ë™øÊï¥Ôºâ
      const mockRoom = {
        id: 'room_' + Date.now(),
        inviteCode: Math.random().toString(36).substring(2, 8).toUpperCase(),
        qrCodeUrl: `https://api.qrserver.com/v1/create-qr-code/?size=200x200&data=CHAP_ROOM_${Date.now()}`,
      };

      setCreatedRoom(mockRoom);
    } catch (error: any) {
      setError(error.message);
    } finally {
      setIsCreating(false);
    }
  };

  const copyInviteCode = () => {
    if (createdRoom) {
      navigator.clipboard.writeText(createdRoom.inviteCode);
      // „Éà„Éº„Çπ„ÉàÈÄöÁü•„Å™„Å©„Åß„Ç≥„Éî„ÉºÂÆå‰∫Ü„ÇíÁü•„Çâ„Åõ„Çã
    }
  };

  const handleJoinRoom = () => {
    if (createdRoom) {
      navigate(`/chat/${createdRoom.id}`);
    }
  };

  // „É´„Éº„É†‰ΩúÊàêÂÆå‰∫ÜÁîªÈù¢
  if (createdRoom) {
    return (
      <Box sx={{ minHeight: '100vh', bgcolor: 'background.default' }}>
        <AppBar position="static">
          <Toolbar>
            <Typography variant="h6" sx={{ flexGrow: 1 }}>
              „É´„Éº„É†‰ΩúÊàêÂÆå‰∫Ü
            </Typography>
          </Toolbar>
        </AppBar>

        <Box sx={{ p: 3 }}>
          <Card sx={{ mb: 3 }}>
            <CardContent sx={{ textAlign: 'center' }}>
              <Typography variant="h5" gutterBottom color="primary">
                üéâ „É´„Éº„É†„Åå‰ΩúÊàê„Åï„Çå„Åæ„Åó„ÅüÔºÅ
              </Typography>
              <Typography variant="body1" color="text.secondary" gutterBottom>
                {roomSettings.title}
              </Typography>
              
              <Box sx={{ my: 3 }}>
                <Typography variant="h6" gutterBottom>
                  ÊãõÂæÖ„Ç≥„Éº„Éâ
                </Typography>
                                 <Box sx={{ display: 'flex', alignItems: 'center', justifyContent: 'center', gap: 1 }}>
                   <Chip
                     label={createdRoom.inviteCode}
                     color="primary"
                     size="medium"
                     sx={{ fontSize: '1.2rem', px: 2 }}
                   />
                   <IconButton onClick={copyInviteCode} color="primary">
                     <ContentCopy />
                   </IconButton>
                 </Box>
              </Box>

              <Button
                variant="outlined"
                startIcon={<QrCode />}
                onClick={() => setShowQRDialog(true)}
                sx={{ mb: 2 }}
              >
                QR„Ç≥„Éº„Éâ„ÇíË°®Á§∫
              </Button>

              <Box sx={{ display: 'flex', gap: 2, justifyContent: 'center' }}>
                <Button
                  variant="contained"
                  onClick={handleJoinRoom}
                  size="large"
                >
                  „É´„Éº„É†„Å´ÂèÇÂä†
                </Button>
                <Button
                  variant="outlined"
                  onClick={() => navigate('/chat-rooms')}
                  size="large"
                >
                  „É´„Éº„É†‰∏ÄË¶ß„Å´Êàª„Çã
                </Button>
              </Box>
            </CardContent>
          </Card>

          {/* „É´„Éº„É†ÊÉÖÂ†± */}
          <Card>
            <CardContent>
              <Typography variant="h6" gutterBottom>
                „É´„Éº„É†Ë®≠ÂÆö
              </Typography>
              <Box sx={{ display: 'grid', gap: 1 }}>
                <Typography variant="body2">
                  <strong>Âà∂ÈôêÊôÇÈñì:</strong> {roomSettings.duration}ÂàÜ
                </Typography>
                <Typography variant="body2">
                  <strong>ÊúÄÂ§ßÂèÇÂä†ËÄÖ:</strong> {roomSettings.maxParticipants}‰∫∫
                </Typography>
                <Typography variant="body2">
                  <strong>ÁØÑÂõ≤:</strong> {roomSettings.locationRadius}km
                </Typography>
                <Typography variant="body2">
                  <strong>„Éó„É©„Ç§„Éô„Éº„Éà:</strong> {roomSettings.isPrivate ? '„ÅØ„ÅÑ' : '„ÅÑ„ÅÑ„Åà'}
                </Typography>
              </Box>
            </CardContent>
          </Card>
        </Box>

        {/* QR„Ç≥„Éº„Éâ„ÉÄ„Ç§„Ç¢„É≠„Ç∞ */}
        <Dialog open={showQRDialog} onClose={() => setShowQRDialog(false)}>
          <DialogTitle>QR„Ç≥„Éº„Éâ</DialogTitle>
          <DialogContent sx={{ textAlign: 'center' }}>
            <img
              src={createdRoom.qrCodeUrl}
              alt="Room QR Code"
              style={{ maxWidth: '100%', height: 'auto' }}
            />
            <Typography variant="body2" color="text.secondary" sx={{ mt: 1 }}>
              „Åì„ÅÆQR„Ç≥„Éº„Éâ„Çí„Çπ„Ç≠„É£„É≥„Åó„Å¶„É´„Éº„É†„Å´ÂèÇÂä†
            </Typography>
          </DialogContent>
          <DialogActions>
            <Button onClick={() => setShowQRDialog(false)}>Èñâ„Åò„Çã</Button>
          </DialogActions>
        </Dialog>
      </Box>
    );
  }

  // „É´„Éº„É†‰ΩúÊàê„Éï„Ç©„Éº„É†
  return (
    <Box sx={{ minHeight: '100vh', bgcolor: 'background.default' }}>
      <AppBar position="static">
        <Toolbar>
          <IconButton
            edge="start"
            color="inherit"
            onClick={() => navigate('/chat-rooms')}
          >
            <ArrowBack />
          </IconButton>
          <Typography variant="h6" sx={{ flexGrow: 1 }}>
            Êñ∞„Åó„ÅÑ„É´„Éº„É†‰ΩúÊàê
          </Typography>
        </Toolbar>
      </AppBar>

      <Box sx={{ p: 3 }}>
        {error && (
          <Alert severity="error" sx={{ mb: 3 }}>
            {error}
          </Alert>
        )}

        <Card sx={{ mb: 3 }}>
          <CardContent>
            <Typography variant="h6" gutterBottom sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
              <Settings />
              Âü∫Êú¨Ë®≠ÂÆö
            </Typography>
            
            <TextField
              fullWidth
              label="„É´„Éº„É†„Çø„Ç§„Éà„É´"
              value={roomSettings.title}
              onChange={(e) => handleInputChange('title', e.target.value)}
              margin="normal"
              required
              placeholder="‰æãÔºöÊ°úÁ•≠„Çä„Ç§„Éô„É≥„Éà"
            />

            <TextField
              fullWidth
              label="Ë™¨ÊòéÔºà‰ªªÊÑèÔºâ"
              value={roomSettings.description}
              onChange={(e) => handleInputChange('description', e.target.value)}
              margin="normal"
              multiline
              rows={2}
              placeholder="„É´„Éº„É†„ÅÆË™¨Êòé„ÇíÂÖ•Âäõ„Åó„Å¶„Åè„Å†„Åï„ÅÑ"
            />
          </CardContent>
        </Card>

        <Card sx={{ mb: 3 }}>
          <CardContent>
            <Typography variant="h6" gutterBottom sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
              <Schedule />
              ÊôÇÈñìË®≠ÂÆö
            </Typography>
            
            <FormControl fullWidth margin="normal">
              <InputLabel>Âà∂ÈôêÊôÇÈñì</InputLabel>
              <Select
                value={roomSettings.duration}
                onChange={(e) => handleInputChange('duration', e.target.value)}
                label="Âà∂ÈôêÊôÇÈñì"
              >
                {durationOptions.map((option) => (
                  <MenuItem key={option.value} value={option.value}>
                    {option.label}
                  </MenuItem>
                ))}
              </Select>
            </FormControl>
          </CardContent>
        </Card>

        <Card sx={{ mb: 3 }}>
          <CardContent>
            <Typography variant="h6" gutterBottom sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
              <People />
              ÂèÇÂä†ËÄÖË®≠ÂÆö
            </Typography>
            
            <FormControl fullWidth margin="normal">
              <InputLabel>ÊúÄÂ§ßÂèÇÂä†ËÄÖÊï∞</InputLabel>
              <Select
                value={roomSettings.maxParticipants}
                onChange={(e) => handleInputChange('maxParticipants', e.target.value)}
                label="ÊúÄÂ§ßÂèÇÂä†ËÄÖÊï∞"
              >
                {participantOptions.map((option) => (
                  <MenuItem key={option} value={option}>
                    {option}‰∫∫
                  </MenuItem>
                ))}
              </Select>
            </FormControl>

            <FormControlLabel
              control={
                <Switch
                  checked={roomSettings.isPrivate}
                  onChange={(e) => handleInputChange('isPrivate', e.target.checked)}
                />
              }
              label="„Éó„É©„Ç§„Éô„Éº„Éà„É´„Éº„É†ÔºàÊãõÂæÖ„Ç≥„Éº„Éâ„ÅåÂøÖË¶ÅÔºâ"
              sx={{ mt: 1 }}
            />
          </CardContent>
        </Card>

        <Card sx={{ mb: 3 }}>
          <CardContent>
            <Typography variant="h6" gutterBottom sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
              <LocationOn />
              ‰ΩçÁΩÆË®≠ÂÆö
            </Typography>
            
            <Typography gutterBottom>
              ÂèÇÂä†ÂèØËÉΩÁØÑÂõ≤: {roomSettings.locationRadius}km
            </Typography>
            <Slider
              value={roomSettings.locationRadius}
              onChange={(_, value) => handleInputChange('locationRadius', value)}
              min={0.1}
              max={10}
              step={0.1}
              marks={[
                { value: 0.5, label: '0.5km' },
                { value: 1, label: '1km' },
                { value: 5, label: '5km' },
                { value: 10, label: '10km' },
              ]}
              valueLabelDisplay="auto"
            />
          </CardContent>
        </Card>

        <Card sx={{ mb: 3 }}>
          <CardContent>
            <Typography variant="h6" gutterBottom>
              „ÉÅ„É£„ÉÉ„ÉàÊ©üËÉΩ
            </Typography>
            
            <FormControlLabel
              control={
                <Switch
                  checked={roomSettings.allowImages}
                  onChange={(e) => handleInputChange('allowImages', e.target.checked)}
                />
              }
              label="ÁîªÂÉèÈÄÅ‰ø°„ÇíË®±ÂèØ"
            />
            
            <FormControlLabel
              control={
                <Switch
                  checked={roomSettings.allowStamps}
                  onChange={(e) => handleInputChange('allowStamps', e.target.checked)}
                />
              }
              label="„Çπ„Çø„É≥„ÉóÈÄÅ‰ø°„ÇíË®±ÂèØ"
            />
          </CardContent>
        </Card>

        <Button
          fullWidth
          variant="contained"
          size="large"
          onClick={handleCreateRoom}
          disabled={isCreating}
          sx={{ py: 2 }}
        >
          {isCreating ? '„É´„Éº„É†‰ΩúÊàê‰∏≠...' : '„É´„Éº„É†„Çí‰ΩúÊàê'}
        </Button>
      </Box>
    </Box>
  );
};

export default CreateRoom;
